import React, { Component } from 'react';
import './App.css';
import { strictEqual } from 'assert';

import HomeScreen from './components/Screens/HomeScreen.jsx'
import ResumeScreen from './components/Screens/ResumeScreen.jsx'
import ScreenTemplate from './components/Screens/ScreenTemplate.jsx'
// import VoiceScreen from './components/Screens/VoiceScreen.jsx'

let UIScreen = {
  quote: '"Great User Experience is Great Business."',
  author: '"Elements of User Experience" by Jesse James Garrett',
  copy1: "The user experience is the most important part of product development. Databases must be accessible, functions must be intuitive to execute, and interfaces must be beautiful and immersive to keep the user coming back and contributing to the growth of the application.",
  copy2: "",
  image3: require("./img/mock2.jpg"),
  image2: require("./img/tempo.jpg"),
  image1: require("./img/mock1.jpg"),
  caption1: "mobile redesign for AlphaVoice, Alexa Skill coverter",
  caption2: "lead design for Tempo, virtual booking agent",
  caption3: "lead design for Mt. Sutro Music Company"
}


let CreativeScreen = {
  image1: require("./img/businesscards.jpg"),
  image2: require("./img/burger.jpg"),
  image3: require("./img/burger.jpg"),
  caption1: "custom business card design",
  caption2: "pixar style burger artwork for a client",
  caption3: ""
}

const Screen = props => {

  return (
    <div className="screen-wrap">
      {(() => {
        switch (props.screen) {
          case 'home':
            return <HomeScreen />
          case 'resume':
            return <ResumeScreen />
          case 'interface':
            return <ScreenTemplate arg={UIScreen} />
          // case 'voice':
          //   return <VoiceScreen arg={VoiceScreen} />
          case 'creative':
            return <ScreenTemplate arg={CreativeScreen} />
          default:
            return <h1>Loading...</h1>;
        }
      })()}
    </div>
  )

}

class App extends Component {

  state = {
    section: "home",
    text: "jello"
  }

  changeToUI = (arg) => {
    this.setState({ section: arg })
  }

  render() {

    return (
      <div className="app">
        <Screen screen={this.state.section} text={this.state.text} />
        <div className="navigate">
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "home")}>
            home
          </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "interface")}>
            ui/ux
          </h1>
          {/* <h1 className="nav-item" onClick={this.changeToUI.bind(this, "voice")}>
            vui/vux
            </h1> */}
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "creative")}>
            creative
            </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "resume")}>
            resume
            </h1>
        </div>
      </div>
    );
  }
}

export default App;
