import React, { Component } from 'react';
import './App.css';
import { strictEqual } from 'assert';

const HomeScreen = props => {
  return (
    <div className="home-wrap">
      <h1><span>Welcome to the portfolio of Kevin Macaraeg</span></h1>
      <img className="avrender" src={require("./img/avrender.png")}></img>
    </div>
  )
}

const ResumeScreen = props => {
  return (
    <div className="about-wrap">
      <div className="resume-headshot">      
      <img src={require("./img/thumbnail.jpg")}></img>
      </div>

      <div className="resume-text"><h1>Kevin Macaraeg is a designer working in lovely San Francisco, California. He is passionate about producing beautiful and enganging web products that create positive change in our ever advancing technological world.</h1>
      <h1>Kevin Macaraeg is a designer working in lovely San Francisco, California. He is passionate about producing beautiful and enganging web products that create positive change in our ever advancing technological world.</h1>
      <h1>Kevin Macaraeg is a designer working in lovely San Francisco, California. He is passionate about producing beautiful and enganging web products that create positive change in our ever advancing technological world.</h1></div>
    </div>
  )
}

const ScreenTemplate = props => {
  return (
    <div className="template-wrap">
      <div className="template-content">
        <div className="template-picture">
        <img src={props.arg.image1}></img>
        <img src={props.arg.image2}></img>
        <img src={props.arg.image3}></img>
        </div>
        <div className="template-text">
          <h1>{props.arg.quote}</h1>
          <h2>{props.arg.author}</h2>
          <p>{props.arg.copy1}</p>
          <p>{props.arg.copy2}</p>
          <p>The tools that I use:</p>
          <div className="icon-box">
            <img src={require("./img/iconai.png")}></img>
            <img src={require("./img/iconps.png")}></img>
            <img src={require("./img/iconid.png")}></img>
            <img src={require("./img/iconsketch.png")}></img>
            <img src={require("./img/iconreact.png")}></img>
          </div>
        </div>
      </div>
    </div>
  )
}

let UIScreen = {
  quote: '"Great User Experience is Great Business."',
  author: '"Elements of User Experience" by Jesse James Garrett',
  copy1: "The user experience is the most important part of product design. Database must be accessible, functions must be intuitive to execute, and interfaces must be beautiful and immersive to keep the user coming back and contributing to the growth of the application.",
  copy2: "I have served as lead designer for development teams and many clients under my personal agency. I understand that visual communication is paramount to keeping a project moving and productive.",
  image3: require("./img/mtsutro2.jpg"),
  image2: require("./img/tempo.jpg"),
  image1: require("./img/alphavoice.jpg"),
}

let VoiceScreen = {
  quote: '"Great User Experience is Great Business."',
  author: '"Designing Voice User Interfaces" by Cathy Pearl',
  copy1: "The speed of computing cannot be held at today's standard. By examining the widespread physical presence and t is irrefutable ",
  copy2: "I have served as lead designer for development teams and many clients under my personal agency. I understand that visual communication is paramount to keeping a project moving and productive.",
  image1: require("./img/businesscards.jpg")
}

let BlockchainScreen = {

}

let CreativeScreen = {

}

const Screen = props => {

  return (
    <div className="screen-wrap">
      {(() => {
        switch (props.screen) {
          case 'home':
            return <HomeScreen />
          case 'resume':
            return <ResumeScreen />
          case 'interface':
            return <ScreenTemplate arg={UIScreen} />
          case 'voice':
            return <ScreenTemplate arg={VoiceScreen} />
            case 'blockchain':
            return <ScreenTemplate arg={BlockchainScreen} />
            case 'creative':
            return <ScreenTemplate arg={CreativeScreen} />
          default:
            return <h1>Didn't Work</h1>;
        }
      })()}
    </div>
  )

}

class App extends Component {

  state = {
    section: "home",
    text: "jello"
  }

  // changeState(val) {
  //   this.setState({section: val})
  //   console.log('jello')
  // }

  changeToUI = (arg) => {
    this.setState({section: arg})
  }

  render() {

    return (
      <div className="app">
        <Screen screen={this.state.section} text={this.state.text} />
        <div className="navigate">
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "home")}>
            home
          </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "interface")}>
            interface/experience
          </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "voice")}>
            voice
            </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "blockchain")}>
            blockchain
            </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "creative")}>
            creative
            </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "resume")}>
            resume
            </h1>
        </div>
      </div>
    );
  }
}

export default App;
