import React, { Component } from 'react';
import styled from "styled-components";

import AboutSection from "./Sections/AboutSection";
import DesignCoinSection from "./Sections/DesignCoinSection";
import Leadership from "./Sections/Leadership";
import Contact from "./Sections/Contact";
import Footer from './Sections/Footer';

const Screen = styled.div`
  z-index: 5;
  h1 {
    text-align: center;
    width: 100%;
    // animation-name: hoverBlue;
    // animation-duration: 1s;
    font-size: 44px;
  }

  @keyframes hoverBlue {
    0% {
      color: black
    }
    100% {
      color: blue
    }
  }

  background-color: rgba(255,255,255,.8)

`

const BackgroundImage = styled.div`

  z-index: 5;
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  height: 100vh;
  width: 100vw;
  transition: 3s ease;
  background: linear-gradient(
    to bottom, 
    ${props => (props.theme.color1)} -30%, 
    rgba(0,0,0,0) 50%);
    display: flex;
    justify-content: center;
    align-items: center;

  animation-name: fadeIn;
  animation-duration: 2s;

  @keyframes fadeIn {
    0% {
      top: 45%;
    },
    100% {
      top: 50%;
    }
  }
`

//   background-image: ${ props => (props.theme ? "url(img/cityillustration_soma_desktop.png)" : "url(img/cityillustration_fidi_desktop.png)")};

// background-image: ${ props => (props.theme = "soma" ? "url(img/cityillustration_soma_desktop.png)" : "url(img/cityillustration_fidi_desktop.png)")};

let City = styled.div`

  background-image: ${ props => (props.theme = "soma" ? "url(img/cityillustration_soma_desktop.png)" : "url(img/cityillustration_fidi_desktop.png)")};

  @media (max-width: 800px) {
    background-image: url(img/cityillustrationmobile.png);
  }

  height: 700px;
  width: 700px;
  background-size: contain;
  background-position: center;
  z-index: 5;
  transition: 1.5s ease;
  background-repeat: no-repeat;

  animation-name: fadeIn;
  animation-duration: 2s;

  @keyframes fadeIn {
    0% {
      opacity: 0;
      filter: blur(10px)
    },
    100% {
      opacity: 1;
      filter: blur(0px)
    }
  }


`

const EmptyDiv = styled.div`

    height: 100vh;

    h1 {
      position: absolute;
      bottom: 50px;
    }

`

const Line = styled.div`

    height: 400px;
    margin: -160px auto;
    z-index: 1;
    width: 1px;
    background: #4d4d4d;
    position: absolute;
    left: 100px;
    animation-name: growshrink;
    animation-duration: 2s;
    animation-iteration-count: infinite;

    @keyframes growshrink {
      0% {
          height: 10px;
          opacity: 0;
      }
      5% {
        opacity: 1;
      }
      25% {
        background: #ff00ff;
      }
      70% {
        opacity: 1;
      }
      75%{
          background: #0000ff;
      }
      100% {
        opacity: 0;
        height: 250px;
      }
    }

`

const ScrollText = styled.div`

    h5 {
      margin-top: -150px;
