import React, { Component } from 'react';
import './App.css';
import { strictEqual } from 'assert';

const HomeScreen = props => {
  return(
    <div className="home-wrap">
      <h1><span>Welcome to the portfolio of Kevin Macaraeg</span></h1>
      <img className="avrender" src={require("./img/avrender.png")}></img>
    </div>
  )
}

const AboutScreen = props => {
  return(
    <div className="about-wrap">
      <h1>Kevin Macaraeg is an independent designer working with a wide ranging clients in the technology space. He is passionate about producing beautiful and enganging web products that create positive change in our ever advancing world.</h1>
    </div>
  )
}

const UIScreen = props => {
  return(
    <div className="ui-wrap">
      <div className="ui-content">
        <div className="ui-picture"></div>
        <div className="ui-text">
        <h1>"Great User Experience is<br></br>Great Business."</h1>
        <h2>Elements of User Experience<span> by Jesse James Garrett</span></h2>
        <p>The user experience is the most important part of product design. Database must be accessible, functions must be intuitive to execute, and interfaces must be beautiful and immersive to keep the user coming back and contributing to the growth of the application.</p>
        <p>My experience includes</p>
        <ul>
          <li>Tempo</li>

        </ul>
        </div>

      </div>
    </div>
  )
}

const Screen = props => {

  return (
    <div className="screen-wrap">
      {(() => {
        switch(props.screen) {
          case 'home':
            return <HomeScreen />
          case 'about':
            return <AboutScreen />
            case 'interface':
            return <UIScreen />
          default:
            return <h1>Didn't Work</h1>;
        }
      })()}
    </div>
  )

}

class App extends Component {

  state = {
    section: 'interface',
    text: "jello"
  }

  // changeState(val) {
  //   this.setState({section: val})
  //   console.log('jello')
  // }

  render() {
    return (
      <div className="app">
          <Screen screen={this.state.section} text={this.state.text}/>
          <div className="navigate">
            <h1 className="nav-item">
              interface/experience
            </h1>
            <h1 className="nav-item">
              voice
            </h1>
            <h1 className="nav-item">
              blockchain
            </h1>
            <h1 className="nav-item">
              creative
            </h1>
            <h1 className="nav-item" >
              resume
            </h1>
          </div>
      </div>
    );
  }
}

export default App;
