import React, { Component } from 'react';
import styled from "styled-components";

const Screen = styled.div`
  z-index: 5;
  h1 {
    text-align: center;
    width: 100%;
    // animation-name: hoverBlue;
    // animation-duration: 1s;
  }

  h1:hover {
    color: yellow
  }

  @keyframes hoverBlue {
    0% {
      color: black
    }
    100% {
      color: blue
    }
  }

  background-color: rgba(255,255,255,.8)

`

const BackgroundImage = styled.div`

  position: fixed;
  top: 50%;
  left: 20%;
  transform: translate(-50%, -50%);
  height: 100vh;
  width: 100vw;
  background: linear-gradient(
    to bottom, 
    rgba(255,0,255,1) -30%, 
    rgba(0,0,0,0) 50%);
    display: flex;
    justify-content: center;
    align-items: center;
`

const City = styled.div`

  background-image: url(img/city01.png);
  height: 700px;
  width: 700px;
  background-size: contain;
  background-position: center;
  z-index: 5;

`

const EmptyDiv = styled.div`

    height: 100vh;

    h1 {
      position: absolute;
      bottom: 50px;
    }

`

const AboutSection = styled.div`

    // transform: skew(0, 15deg);

    min-height: 100vh;
    display: flex;
    justify-content: center;
    background-color: white;
    min-width: 100%;

    .color {
      margin-top: 100px;
      z-index: 0;
      width: 100vw;
      height: 100vh;
      background-color: white;
      background: linear-gradient(
        to bottom,
        rgba(0,0,255,1), 
        rgba(255,0,255,.0));
        z-index: 5;
      display: flex;
      justify-content: center;
      align-items: center;
      transform: skew(0,-5deg);
    }

    .text {
      z-index: 20;
      background-color: rgba(255,255,255,.95);
      // background: red;
      width: 100vw;
      padding: 300px 0 400px;
      display: flex;
      justify-content: center;
      transition. 3s ease;
      transform: skew(0,5deg);


      h3 {
        transform: skew(0,-5deg);
        font-family: 'Roboto Slab', serif;
        font-weight: 300;
        font-size: 36px;
        line-height: 54px;

        max-width: 800px;


        span {


        }

        .gray {
          color: #4d4d4d;
        }

        .bold {
          font-weight: 700;
          transition: .5s ease;
          border-bottom: 1px solid rgba(0,0,0,0);
          background: -webkit-linear-gradient(-30deg, #0000ff, #ff00ff 70%);
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        }

        .bold:hover {
          border-bottom: 1px solid #ff00ff;
        }

        .montserrat {
          font-family: 'Montserrat';
          font-weight: 700;
          padding-right: 3px;
        }
      }

    }


    .textAlt {
      margin-top: -150px;
      z-index: 20;
      background-color: rgba(244,244,244,1);
      // background: red;
      width: 100vw;
      padding: 300px 0;
      display: flex;
      justify-content: center;
      transition. 3s ease;
      transform: skew(0,-5deg);

      h3 {
        transform: skew(0,5deg);
        font-family: 'Roboto Slab', serif;
        font-weight: 300;
        font-size: 36px;
        line-height: 54px;

        max-width: 800px;

        span {


        }

        .gray {
          color: #4d4d4d;
          background: none;
        }

        .bold {
          font-family: 'Montserrat';
          margin-right:6px;
          font-weight: 700;
          transition: .5s ease;
          border-bottom: 1px solid rgba(0,0,0,0);
          background: -webkit-linear-gradient(-30deg, #0000ff, #ff00ff 70%);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
        }

        .bold:hover {
          border-bottom: 1px solid #ff00ff;
        }


        .montserrat {
          font-family: 'Montserrat';
          font-weight: 700;
          padding-right: 3px;
        }
      }

    }

    .alt {


      transform: skew(0,-5deg);
      background: linear-gradient(
        30deg,
        rgba(0,0,255,1), 
        rgba(255,0,255,1));
        z-index: 5;

      h3 {
        color: white !important;
      }
    }

`

const MovingText = styled.div`

    text-align: center;

    // animation-name: scroll;
    // animation-duration: 2s;
    // animation-iteration-count: infinite;

    @keyframes scroll {
      0% {
      left: 0;
      opacity: 0;
      }

      50%{

        opacity: 1
      }

      100% {
      left: 100%;
      opacity: 0;
      }
    }


`

const DesignCoinSection = styled.div`

    postition: absolute;

`

const Line = styled.div`

    height: 200px;
    margin: -100px auto;
    z-index: 1;
    width: 1px;
    background: #4d4d4d;
    position: absolute;
    left: 50%;
    animation-name: growshrink;
    animation-duration: 2s;
    animation-iteration-count: infinite;

    @keyframes growshrink {
      0% {
          height: 10px;
          opacity: 0;
          background: #0000ff;
      }
      5% {
        opacity: 1;
      }
      25% {
        background: #ff00ff;
      }
      70% {
        opacity: 1;
      }
      75%{
          height: 100px;
          background: #0000ff;
      }
      100% {
        opacity: 0;
      }
    }

`


const HomeScreen = props => {
  return (
    <Screen>

      <MovingText>

      </MovingText>
      <EmptyDiv>
      </EmptyDiv>
      <AboutSection>

        <div className="frames">


        <Line></Line>

          <div className="color">

          </div>

          <BackgroundImage>
            <City></City>
          </BackgroundImage>



          <div className="text">
            <h3>
              <span className="bold">What is Decentralized Design?
                    </span><br></br><span className="gray">
                    Not sure! But it's catchy and you thought it was interesting enough to keep reading.</span></h3>
          </div>

          <div className="textAlt">
            <h3>
              <span className="bold">DESIGNCOIN
                    </span>
                    <span className="gray"> is a web design agency servicing startups, crypto companies, and small businesses based in San Francisco, California. We make everything in house with pride and diligence.</span></h3>
          </div>

        </div>

      </AboutSection>

      <DesignCoinSection>
        <h1>Test</h1>
      </DesignCoinSection>



    </Screen>
  )
}

export default HomeScreen
