import React, { Component } from 'react';
import styled from "styled-components";
import ReactContactForm from 'react-mail-form';

const Screen = styled.div`
  z-index: 5;
  h1 {
    text-align: center;
    width: 100%;
    // animation-name: hoverBlue;
    // animation-duration: 1s;
    font-size: 44px;
  }

  @keyframes hoverBlue {
    0% {
      color: black
    }
    100% {
      color: blue
    }
  }

  background-color: rgba(255,255,255,.8)

`

const BackgroundImage = styled.div`

  z-index: 5;
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  height: 100vh;
  width: 100vw;
  transition: 3s ease;
  background: linear-gradient(
    to bottom, 
    rgba(255,0,255,1) -30%, 
    rgba(0,0,0,0) 50%);
    display: flex;
    justify-content: center;
    align-items: center;

  animation-name: fadeIn;
  animation-duration: 2s;

  @keyframes fadeIn {
    0% {
      top: 45%;
    },
    100% {
      top: 50%;
    }
  }
`

const City = styled.div`

  background-image: ${ props => (props.institutional ? "url(img/cityillustrationinstitutionaldesktop.png)" : "url(img/cityillustrationdesk.png)")};


  @media (max-width: 800px) {
    background-image: url(img/cityillustrationmobile.png);
  }

  height: 700px;
  width: 700px;
  background-size: contain;
  background-position: center;
  z-index: 5;
  transition: 1.5s ease;
  background-repeat: no-repeat;

  animation-name: fadeIn;
  animation-duration: 2s;

  @keyframes fadeIn {
    0% {
      opacity: 0;
      filter: blur(10px)
    },
    100% {
      opacity: 1;
      filter: blur(0px)
    }
  }


`

const EmptyDiv = styled.div`

    height: 100vh;

    h1 {
      position: absolute;
      bottom: 50px;
    }

`

const AboutSection = styled.div`

    min-height: 100vh;
    display: flex;
    justify-content: center;
    flex-direction: column;
    background-color: white;
    min-width: 100%;

    @media(max-width: 800px){
      padding: 100px 0 300px;
      h3 {
        font-size: 20px !important;
        line-height: 30px !important;
        margin: 25px;
      }
    }

    .color {
      margin-top: 100px;
      z-index: 1;
      width: 100vw;
      height: 100vh;
      background-color: white;
      background: linear-gradient(
        to bottom,
        rgba(0,0,255,1), 
        rgba(255,0,255,.0));
        z-index: 5;
      display: flex;
      justify-content: center;
      align-items: center;
      transform: skew(0,-5deg);
      // background-image: url(./img/wavydesk.png);

        img {
          height: 500px;
        }

    }

    .text {

      z-index: 10;
      background-color: rgba(255,255,255,.85);
      // background: red;
      width: 100vw;
      padding: 300px 0 450px;
      display: flex;
      justify-content: center;
      transition. 3s ease;
      transform: skew(0,5deg);

      @media(max-width: 800px){
        padding: 100px 0 200px;
      }

      h3 {
        transform: skew(0,-5deg);
        font-weight: 300;
        font-size: 36px;
        line-height: 54px;
        max-width: 800px;
        font-family: 'Roboto Slab';

        .gray {
          color: #4d4d4d;
        }

        .bold {
          font-family: 'Montserrat';
          font-weight: 700;
          transition: .5s ease;
          border-bottom: 1px solid rgba(0,0,0,0);
          background: -webkit-linear-gradient(-30deg, #0000ff, #ff00ff 70%);
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        }

        .bold:hover {
          border-bottom: 1px solid #ff00ff;
        }

        .montserrat {
          font-family: 'Montserrat';
          font-weight: 700;
          padding-right: 3px;
        }
      }

    }

    .textAlt {

      @media(max-width: 800px){
        padding: 100px 0 100px;
        h3 {
          font-size: 20px !important;
          line-height: 30px !important;
          margin: 25px;
        }
      }


      margin-top: -150px;
      z-index: 20;
      background-color: rgba(244,244,244,1);
      // background: red;
      width: 100vw;
      padding: 300px 0;
      display: flex;
      flex-direction: column;
      justify-content: center;
      align-items: center;
      transition. 3s ease;
      transform: skew(0,-5deg);



      h3 {
        transform: skew(0,5deg);
        font-family: 'Roboto Slab', serif;
        font-weight: 300;
        font-size: 36px;
        line-height: 54px;

        max-width: 800px;

        span {


        }

        .gray {
          color: #4d4d4d;
          background: none;
        }

        .bold {
          font-family: 'Montserrat';
          margin-right:6px;
          font-weight: 700;
          transition: .5s ease;
          border-bottom: 1px solid rgba(0,0,0,0);
          background: -webkit-linear-gradient(-30deg, #0000ff, #ff00ff 70%);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
        }

        .bold:hover {
          border-bottom: 1px solid #ff00ff;
        }


        .montserrat {
          font-family: 'Montserrat';
          font-weight: 700;
          padding-right: 3px;
        }
      }

    }

    .alt {


      transform: skew(0,-5deg);
      background: linear-gradient(
        30deg,
        rgba(0,0,255,1), 
        rgba(255,0,255,1));
        z-index: 5;

      h3 {
        color: white !important;
      }
    }

`

const Line = styled.div`

    height: 400px;
    margin: -160px auto;
    z-index: 1;
    width: 1px;
    background: #4d4d4d;
    position: absolute;
    left: 100px;
    animation-name: growshrink;
    animation-duration: 2s;
    animation-iteration-count: infinite;

    @keyframes growshrink {
      0% {
          height: 10px;
          opacity: 0;
      }
      5% {
        opacity: 1;
      }
      25% {
        background: #ff00ff;
      }
      70% {
        opacity: 1;
      }
      75%{
          background: #0000ff;
      }
      100% {
        opacity: 0;
        height: 250px;
      }
    }

`

