const ScrollText = styled.div`

    h5 {
      margin-top: -150px;
      transform: rotate(-90deg) translate(100px, 38px);
      color: #ff00ff;
    }

`

const ScrollItems = styled.div`

@media(max-width: 800px) {
  display: none;
}

  position: absolute;
  right: 60px;
  display: flex;
  flex-direction: column;

`

const DesignCoinSection = styled.div`

    padding: 250px 0 200px;
    z-index: 20;
    width: 100vw;
    display: flex;
    flex-direction: column;
    justify-content: center;
    // background-image: url(./img/wavydesk.png);

    @media(max-width: 800px) {
      h3, h4 {
        font-size: 20px !important;
        margin: 10px !important;
      }
      h1 {
        font-size: 30px !important;
        margin: 0;
      }
      padding: 100px 0 100px;
    }

    h1 {
      font-size: 48px;
      margin-bottom: 30px;
    }

    h3 {
      font-size: 32px;
      margin-bottom: 30px;
      font-weight: 700;
    }

    h4 {
      margin-bottom: 30px;
      font-size: 24px;
      font-family: "Roboto Slab";
      font-weight: 300;
    }

    .categoryBox {
      display: flex;
      flex-direction: column;
      align-items: center;
      margin: 20px;
    }

    .categories {

      margin: 0 auto;
      padding: 10px;
      width: auto;
      display: flex;

      @media (max-width: 800px) {
        flex-direction: column;
      }

    }

    .servicesOffered {
      padding: 70px 30px 0;
      box-shadow: 0px 0px 5px rgba(0,0,0,.3);
      border-radius: 10px;
      max-width: 900px;
      margin: 0 auto;
      background: linear-gradient(to top, rgba(0,0,255,.1), rgba(255,0,255,.1) 3%,rgba(0,0,0,0) 10%)

    }

    img {
      height: 150px;
      margin: 0 auto;
      text-align: center;

      @media (max-width: 800px) {
        height: 90px;
        margin: 30px auto 0 !important;
      }

    }

    .ferry {
      display: flex;
      justify-content: center;
    }

`

const Footer = styled.div`

  position: relative;
  display: flex;
  flex-direction: column;
  justify-content: center;
  background-color: #f1f1f1;
  width: 100%;
  z-index: 50;

  min-height: 200px;

  img {
    height: 50px;
    margin: 0 auto;
    margin-bottom: 20px;
  }

  h3 {
    text-align: center;

  }

  @media (max-width: 800px) {
    min-height: 100vh;
    margin-bottom: -100vh;
    h3 {
      font-size: 20px !important;
    }
    img {
      margin-bottom: 0 !important;
    }
  }

`

const Leadership = styled.div`

  // position: absolute;
  background-image: url(./img/wavy.png);
  min-height: 500px;
  width: 100vw;
  background-size: cover;
  padding: 200px 0;
  display; flex;
  flex-direction: column;
  justify-content: center;
  margin: 0 auto;
  color: white;
  background-repeat: no-repeat;
  z-index: 10;

  @media(max-width: 800px) {
    h3, h4 {
      font-size: 20px !important;
      line-height: 30px;
      margin: 10px !important;
    }
    h1 {
      font-size: 30px !important;
      margin: 0;
    }
    padding: 100px 0 0px;
  }

  h1 {
    font-size: 44px;
    color: white;
    margin-bottom: 20px;
  }

  a {
    transition: .2s ease;
  }

  a:hover {
    color: #ff00ff;
  }

  .headshot {
    background: url(./img/thumbnail.jpg);
    height: 200px;
    width: 200px;
    background-size: cover;
    background-position: center;
    border-radius: 200px;
    margin: 10px auto 40px;
  };

  .bio {
    padding: 20px 50px;
    max-width: 600px;
    margin: 0 auto;
    h3 {
      font-size: 25px;
      line-height: 40px;
      font-family: "Roboto Slab";
      font-weight: 300;
    }

    @media (max-width: 800px) {
      padding: 20px !important;

    }
  }

  .kevin {

    max-width: 880px;
    display: flex;
    margin: 50px auto 0;
    flex-direction: column;
    justify-content: center;
    border: 1px solid white;
    padding: 40px 0;
