import React, { Component } from 'react';
import styled from "styled-components";

import AboutSection from "./Sections/AboutSection";
import DesignCoinSection from "./Sections/DesignCoinSection";
import Leadership from "./Sections/Leadership";
import Contact from "./Sections/Contact";
import Footer from './Sections/Footer';
import Navigator from './../navigator'


const ProgressBar = styled.div`
    height: 18px;
    background: ${props => (props.theme == "soma" ? props.color.soma4 : props.color.fidi4)};
    width: 0%;
    transition: .2s ease;
`;

const Content = styled.div`

`

const Container = styled.div`
    width: 100%;
    height: 18px;
    background: ${props => (props.theme == "soma" ? props.color.soma5 : props.color.fidi5)};
    position: fixed;
    top: 20px;
    left: 0px;
    z-index: 100;
    opacity: 0;
    transition: 1s ease;
`;

class ScrollProgress extends Component {

state = {
  theme: this.props.theme,
}

  componentDidMount() {
    window.onscroll = function () {
      let winScroll =
        document.body.scrollTop || document.documentElement.scrollTop;
      let height =
        document.documentElement.scrollHeight -
        document.documentElement.clientHeight;
      let scrolled = (winScroll / height) * 100;

      const barCont = document.getElementById("progressBarCont");
      const bar = document.getElementById("progressBar");

      if (scrolled === 0) {
        barCont.style.opacity = "0";
      } else {
        barCont.style.opacity = "1";
        bar.style.width = scrolled + "%";
      }

      console.log(scrolled)

      const city = document.getElementById("city");

      if (scrolled > 30) {
        city.style.opacity = "0";

      } else {
        city.style.opacity = "1";

      }

      const cityWrap = document.getElementById("cityWrap");

      if (scrolled > 25) {
        cityWrap.style.top = "47%";
        cityWrap.style.opacity = "0";
        // city.style.display = "none";
      } else {
        cityWrap.style.top = "50%";
        cityWrap.style.opacity = "1";
        // city.style.display = "flex";
      }

    };
  }

  render() {
    return (
      <Container id="progressBarCont" theme={this.state.theme} color={this.props.color}>
        <ProgressBar id="progressBar"  theme={this.state.theme} color={this.props.color}/>
      </Container>
    );
  }
}

const Screen = styled.div`
  z-index: 5;
  h1 {
    text-align: center;
    width: 100%;
    // animation-name: hoverBlue;
    // animation-duration: 1s;
    font-size: 44px;
  }

  @keyframes hoverBlue {
    0% {
      color: black
    }
    100% {
      color: blue
    }
  }

  background-color: rgba(255,255,255,.8)

`

const BackgroundImage = styled.div`

  z-index: 5;
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  height: 100vh;
  width: 100vw;
  transition: 3s ease;
  background: linear-gradient(
    to bottom, 
    ${props => (props.theme == "soma" ? props.color.soma1 : props.color.fidi1)} -30%, 
    rgba(0,0,0,0) 50%);
    display: flex;
    justify-content: center;
    align-items: center;

  animation-name: fadeIn;
  animation-duration: 2s;

  @keyframes fadeIn {
    0% {
      top: 45%;
    },
    100% {
      top: 50%;
    }
  }
`

//   background-image: ${ props => (props.theme ? "url(img/cityillustration_soma_desktop.png)" : "url(img/cityillustration_fidi_desktop.png)")};

// background-image: ${ props => (props.theme = "soma" ? "url(img/cityillustration_soma_desktop.png)" : "url(img/cityillustration_fidi_desktop.png)")};

let City = styled.div`

  background-image: ${ props => (props.theme == "soma" ? "url(img/cityillustration_soma_desktop.png)" : "url(img/cityillustration_fidi_desktop.png)")};

  @media (max-width: 800px) {
    background-image: url(img/cityillustrationmobile.png);
  }

  height: 700px;
  width: 700px;
  background-size: contain;
  background-position: center;
  z-index: 5;
  transition: 1.5s ease;
  background-repeat: no-repeat;

  animation-name: fadeIn;
  animation-duration: 2s;

  @keyframes fadeIn {
    0% {
      opacity: 0;
      filter: blur(10px)
    },
    100% {
      opacity: 1;
      filter: blur(0px)
    }
  }


`

const EmptyDiv = styled.div`

    height: 100vh;

    h1 {
      position: absolute;
      bottom: 50px;
    }

`

const Line = styled.div`

    height: 400px;
    margin: -160px auto;
    z-index: 1;
    width: 1px;
    background: #4d4d4d;
    position: absolute;
    left: 100px;
    animation-name: ${props => (props.theme == "soma" ? "growshrink1" : "growshrink2")};
    animation-duration: 2s;
    animation-iteration-count: infinite;


    @keyframes growshrink1 {
      0% {
          height: 10px;
          opacity: 0;
      }
      5% {
        opacity: 1;
      }
      25% {
        background: #ff00ff;
      }
      70% {
        opacity: 1;
      }
      75%{
          background: #0000ff;
      }
      100% {
        opacity: 0;
        height: 250px;
      }
    }

    @keyframes growshrink2 {
      0% {
          height: 10px;
          opacity: 0;
      }
      5% {
        opacity: 1;
      }
      25% {
        background: #c7dcdd;
      }
      70% {
        opacity: 1;
      }
      75%{
          background: #4e6a91;
      }
      100% {
        opacity: 0;
        height: 250px;
      }
    }

`

const ScrollText = styled.div`

    h5 {
      margin-top: -150px;
      transform: rotate(-90deg) translate(100px, 38px);
      color: ${props => (props.theme == "soma" ? props.color.soma1 : props.color.fidi1)};
    }

`

const ScrollItems = styled.div`

@media(max-width: 800px) {
  display: none;
}

  position: absolute;
  right: 60px;
  display: flex;
  flex-direction: column;

`

const ThemeChanger = styled.div`

  position: fixed;
  display: flex;
  z-index: 900;
  left: 40px;
  bottom: 40px;
  border-radius: 5px;

  .themeTitle {
    font-size: 15px;
    padding: 10px 10px;
    opacity: 0;
    margin-left: -20px;
    transition: .2s ease;
  }

  button:hover {
    background-color: ${props => (props.soma ? "rgba(255,204,255,1)" : "rgba(199,220,221,1)")};
  }

  button:hover ~ .themeTitle{
    opacity: 1;
    margin-left: 0px;
  }

`

const ThemeButtons = styled.button`

  font-size: 15px;
  color: black;
  border-radius: ${props => (props.soma ? "5px 0 0 5px" : "0 5px 5px 0")};
  padding: 5px 10px;
  border: none;
  font-family: "Montserrat";
  box-shadow: 0px 5px 5px rgba(0,0,0,.1);

`

const ThemeTitle = styled.h3`


`


