import React, { Component } from 'react';
import './App.css';
import { strictEqual } from 'assert';

import HomeScreen from './components/Screens/HomeScreen.jsx'
import ResumeScreen from './components/Screens/ResumeScreen.jsx'
import ScreenTemplate from './components/Screens/ScreenTemplate.jsx'
// import VoiceScreen from './components/Screens/VoiceScreen.jsx'

let UIScreen = {
  quote: '"Great User Experience is Great Business."',
  author: '"Elements of User Experience" by Jesse James Garrett',
  title1: "Don't Let Them Think",
  copy1: "Effective user experiences lie at the cross section of user desires and business goals. Steve Krug, author of the web design bible: “Don’t Make Me Think”, declares the title as Krug’s First Law of Usability. It is the role of the designer to create paths that allow users to frictionlessly access databases, execute functions, and become comfortably immersed in a welcoming environment. When the user receives what they want and the business delivers the best of their promise, a powerful symbiotic relationship forms that moves both parties into healthy positive growth.",
  title2: "Designing the Future",
  copy2: 'If the only thing that is certain is uncertainty, then any successful career must be focused on reading trends, reacting to market shifts, and pivoting toward powerful and emerging industries. I am in the business of identifying uncharted waters and designing bleeding edge technologies that will create positive change for society.',
  image3: require("./img/mock2.jpg"),
  image2: require("./img/tempo.jpg"),
  image1: require("./img/mock1.jpg"),
  caption1: "mobile redesign for AlphaVoice, video marketing agency",
  caption2: "lead design for Tempo, virtual booking agent",
  caption3: "lead design for Mt. Sutro Music Company",
  linkTitle: "The Future of Humanity with Yuval Noah Harari",
  externalLink: "https://www.youtube.com/watch?v=XOmQqBX6Dn4",
}


let CreativeScreen = {
  quote: '"Have no fear of perfection. You will never reach it."',
  author: 'Salvador Dali',
  title1: "",
  copy1: "The life of the artist is one that is often warned against: unstable environment, subjectively judged, and impractical to sustain most lifestyles. However, we can observe through history that technology is a creative-driven industry. Innovative technologies are built on unstable environments, subjective judgement calls, and impractical die-hard commitment. This dichotomy defines the modern era; the success of one is codependent on the success of the other. If the balance is off center, the ideas will not proliferate. When both forces are controlled, the world will change.",
  copy2: "Creativity is two parts: linking ideas and communicating them. There is no self, only the expressions of the ideas passed onto you. Communicating requires technical skill to properly articulate through a given form of media. Creativity is about linking the ideas that have impacted you and properly articulating them though a powerful form of media. Creativity is a muscle that needs to be actively exercised.",
  image2: require("./img/businesscards.jpg"),
  image1: require("./img/burger.jpg"),
  image3: require("./img/jackwhite.jpg"),
  caption2: "custom business card design",
  caption1: "pixar style burger artwork for a client",
  caption3: "music poster illustration",
  linkTitle: "I make music too!",
  externalLink: "https://soundcloud.com/thedaphnes/sunday",
}

const Screen = props => {

  return (
    <div className="screen-wrap">
      {(() => {
        switch (props.screen) {
          case 'home':
            return <HomeScreen />
          case 'resume':
            return <ResumeScreen />
          case 'interface':
            return <ScreenTemplate arg={UIScreen} />
          // case 'voice':
          //   return <VoiceScreen arg={VoiceScreen} />
          case 'creative':
            return <ScreenTemplate arg={CreativeScreen} />
          default:
            return <h1>Loading...</h1>;
        }
      })()}
    </div>
  )

}

class App extends Component {

  state = {
    section: "home",
    text: "jello"
  }

  changeToUI = (arg) => {
    this.setState({ section: arg })
  }

  render() {

    return (
      <div className="app">
        <Screen screen={this.state.section} text={this.state.text} />
        <div className="navigate">
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "home")}>
            home
          </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "interface")}>
            design
          </h1>
          {/* <h1 className="nav-item" onClick={this.changeToUI.bind(this, "voice")}>
            vui/vux
            </h1> */}
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "creative")}>
            creative
            </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "resume")}>
            resume
            </h1>
        </div>
      </div>
    );
  }
}

export default App;
