import React, { Component } from 'react';
import styled from "styled-components";

const Screen = styled.div`
  z-index: 5;
  h1 {
    text-align: center;
    width: 100%;
    // animation-name: hoverBlue;
    // animation-duration: 1s;
  }

  h1:hover {
    color: yellow
  }

  @keyframes hoverBlue {
    0% {
      color: black
    }
    100% {
      color: blue
    }
  }

`

const BackgroundImage = styled.div`

  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  height: 100vh;
  width: 100vw;
  background: linear-gradient(
    to bottom, 
    rgba(255,0,255,1) -30%, 
    rgba(0,0,0,0) 50%);
    display: flex;
    justify-content: center;
    align-items: center;
`

const City = styled.div`

  background-image: url(img/city01.png);
  height: 700px;
  width: 700px;
  background-size: contain;
  background-position: center;

`

const EmptyDiv = styled.div`

    height: 100vh;

    h1 {
      position: absolute;
      bottom: 50px;
    }

`

const AboutSection = styled.div`

    // transform: skew(0, 15deg);

    min-height: 100vh;
    display: flex;
    justify-content: center;

    .color {
      height: 500px;
      max-width: 800px;
      width: 100%;
      background: linear-gradient(
        30deg, 
        rgba(255,0,255,1), 
        rgba(0,0,255,1));
        z-index: 5;
      display: flex;
      justify-content: center;
      align-items: center;
    }

    .text {
      background-color: #fff;
      z-index: 10;
      position: relative;

      h3 {
        font-family: 'Roboto Slab', serif;
        font-weight: 300;
        font-size: 36px;
        line-height: 54px;
        background: -webkit-linear-gradient(-30deg, #0000ff, #ff00ff 70%);
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;

        span {
          font-weight: 700;

        }

        .bold {

        }

        .montserrat {
          font-family: 'Montserrat';
          font-weight: 700;
          padding-right: 3px;
        }
      }

    }

`

const MovingText = styled.div`

    text-align: center;

    // animation-name: scroll;
    // animation-duration: 2s;
    // animation-iteration-count: infinite;

    @keyframes scroll {
      0% {
      left: 0;
      opacity: 0;
      }

      50%{

        opacity: 1
      }
      100% {
      left: 100%;
      opacity: 0;
      }
    }


`

const HomeScreen = props => {
    return (
        <Screen>
          <BackgroundImage>
            <City></City>
          </BackgroundImage>
          <MovingText>

          </MovingText>
          <EmptyDiv>     
          {/* <h1>DECENTRALIZED VISUAL DESIGN</h1>        */}
            </EmptyDiv>
          <AboutSection>

            <div className="frames">

              <div className="color">
                <div className="text">
                  <h3><span className="bold">What is Decentralized Design? 
                    </span> Not sure!<br></br>But it's catchy and you thought it was interesting enough to keep reading. <span className="montserrat">DESIGNCOIN
                    </span>  is a web design agency based out of San Francisco, California. We make everything in house with pride and diligence.</h3>
                </div>
              </div>


            </div>

          </AboutSection>
        </Screen>
    )
  }

export default HomeScreen
