import React, { Component } from 'react';
import './App.css';
import { strictEqual } from 'assert';
import styled from 'styled-components'

import HomeScreen from './components/Screens/HomeScreen.jsx'
const Screen = props => {

  return (
    <div className="screen-wrap">
      {(() => {
        switch (props.screen) {
          case 'home':
            return <HomeScreen />
          default:
            return <h1>Loading...</h1>;
        }
      })()}
    </div>
  )

}

const Content = styled.div`

`

const Navigator = styled.div`
  display: flex;
  justify-content: space-between;
  width: 100vw;
  h4 {
    padding: 5px 0;
    transition: .2s ease;
    font-size: 10px;
    letter-spacing: 2px;
    border-bottom: 1px solid rgba(0,0,0,0);
    margin: 10px 5px;
    max-height: 13px;
    font-weight: 900;
  }
  .links {
    display: flex;
    padding: 20px;
  }
  .home {
    display: flex;

  }
  img {
    height: 50px;
    padding: 10px;
    transition: .2s ease;
  }
  .logotext {
    opacity: 0;
    transition: .3s ease;
  }
  img:hover ~ .logotext {
    opacity: 1
  }
  h4:hover {
    cursor: pointer;
    // background: -webkit-linear-gradient(#eee, #333);
    // -webkit-background-clip: text;
    // -webkit-text-fill-color: transparent;
    border-bottom: 1px solid rgba(0,0,0,1);
  }
`

class App extends Component {

  state = {
    section: "home",
    text: "jello"
  }

  changeToUI = (arg) => {
    this.setState({ section: arg })
  }

  render() {

    return (
      <Content>
          <Navigator>
            <div className="home">
              <img src="./img/designcoinlogo.png"/>
              <h4 className="logotext">DESIGNCOIN</h4>
            </div>
            <div className="links">
              <h4>PROJECTS</h4>
              <h4>RESUME</h4>
              <h4>CONTACT</h4>
            </div>
          </Navigator>
          <Screen screen={this.state.section} text={this.state.text} />
          {/* <div className="navigate">
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "home")}>
            home
          </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "interface")}>
            design
          </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "creative")}>
            creative
            </h1>
          <h1 className="nav-item" onClick={this.changeToUI.bind(this, "resume")}>
            resume
            </h1>
        </div> */}
      </Content>


    );
  }
}

export default App;
