import React, { Component } from 'react';
import styled from "styled-components";

const Screen = styled.div`
  z-index: 5;
  h1 {
    text-align: center;
    width: 100%;
    // animation-name: hoverBlue;
    // animation-duration: 1s;
  }

  h1:hover {
    color: yellow
  }

  @keyframes hoverBlue {
    0% {
      color: black
    }
    100% {
      color: blue
    }
  }

  background-color: rgba(255,255,255,.8)

`

const BackgroundImage = styled.div`

  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  height: 100vh;
  width: 100vw;
  transition: 3s ease;
  background: linear-gradient(
    to bottom, 
    rgba(255,0,255,1) -30%, 
    rgba(0,0,0,0) 50%);
    display: flex;
    justify-content: center;
    align-items: center;
`

const City = styled.div`

  background-image: url(img/city01.png);
  height: 700px;
  width: 700px;
  background-size: contain;
  background-position: center;
  z-index: 5;
  transition: 1.5s ease;

`

const EmptyDiv = styled.div`

    height: 100vh;

    h1 {
      position: absolute;
      bottom: 50px;
    }

`

const AboutSection = styled.div`

    // transform: skew(0, 15deg);

    min-height: 100vh;
    display: flex;
    justify-content: center;
    background-color: white;
    min-width: 100%;

    .color {
      margin-top: 100px;
      z-index: 0;
      width: 100vw;
      height: 100vh;
      background-color: white;
      background: linear-gradient(
        to bottom,
        rgba(0,0,255,1), 
        rgba(255,0,255,.0));
        z-index: 5;
      display: flex;
      justify-content: center;
      align-items: center;
      transform: skew(0,-5deg);
      // background-image: url(./img/wavy.png);

        img {
          height: 500px;
        }

    }

    .text {
      z-index: 20;
      background-color: rgba(255,255,255,.75);
      // background: red;
      width: 100vw;
      padding: 300px 0 400px;
      display: flex;
      justify-content: center;
      transition. 3s ease;
      transform: skew(0,5deg);


      h3 {
        transform: skew(0,-5deg);
        font-family: 'Roboto Slab', serif;
        font-weight: 300;
        font-size: 36px;
        line-height: 54px;

        max-width: 800px;


        span {


        }

        .gray {
          color: #4d4d4d;
        }

        .bold {
          font-weight: 700;
          transition: .5s ease;
          border-bottom: 1px solid rgba(0,0,0,0);
          background: -webkit-linear-gradient(-30deg, #0000ff, #ff00ff 70%);
        -webkit-background-clip: text;
        -webkit-text-fill-color: transparent;
        }

        .bold:hover {
          border-bottom: 1px solid #ff00ff;
        }

        .montserrat {
          font-family: 'Montserrat';
          font-weight: 700;
          padding-right: 3px;
        }
      }

    }


    .textAlt {
      margin-top: -150px;
      z-index: 20;
      background-color: rgba(244,244,244,1);
      // background: red;
      width: 100vw;
      padding: 300px 0;
      display: flex;
      justify-content: center;
      transition. 3s ease;
      transform: skew(0,-5deg);

      h3 {
        transform: skew(0,5deg);
        font-family: 'Roboto Slab', serif;
        font-weight: 300;
        font-size: 36px;
        line-height: 54px;

        max-width: 800px;

        span {


        }

        .gray {
          color: #4d4d4d;
          background: none;
        }

        .bold {
          font-family: 'Montserrat';
          margin-right:6px;
          font-weight: 700;
          transition: .5s ease;
          border-bottom: 1px solid rgba(0,0,0,0);
          background: -webkit-linear-gradient(-30deg, #0000ff, #ff00ff 70%);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
        }

        .bold:hover {
          border-bottom: 1px solid #ff00ff;
        }


        .montserrat {
          font-family: 'Montserrat';
          font-weight: 700;
          padding-right: 3px;
        }
      }

    }

    .alt {


      transform: skew(0,-5deg);
      background: linear-gradient(
        30deg,
        rgba(0,0,255,1), 
        rgba(255,0,255,1));
        z-index: 5;

      h3 {
        color: white !important;
      }
    }

`

const MovingText = styled.div`

    text-align: center;

    // animation-name: scroll;
    // animation-duration: 2s;
    // animation-iteration-count: infinite;

    @keyframes scroll {
      0% {
      left: 0;
      opacity: 0;
      }

      50%{

        opacity: 1
      }

      100% {
      left: 100%;
      opacity: 0;
      }
    }


`

const Line = styled.div`

    height: 400px;
    margin: -200px auto;
    z-index: 1;
    width: 1px;
    background: #4d4d4d;
    position: absolute;
    left: 100px;
    animation-name: growshrink;
    animation-duration: 2s;
    animation-iteration-count: infinite;

    @keyframes growshrink {
      0% {
          height: 10px;
          opacity: 0;
      }
      5% {
        opacity: 1;
      }
      25% {
        background: #ff00ff;
      }
      70% {
        opacity: 1;
      }
      75%{
          background: #0000ff;
      }
      100% {
        opacity: 0;
        height: 250px;
      }
    }

`

const ScrollText = styled.div`

    h5 {
      margin-top: -190px;
      transform: rotate(-90deg) translate(100px, 38px);
      color: #ff00ff;
    }

`

const ScrollItems = styled.div`

  position: absolute;
  right: 60px;
  display: flex;
  flex-direction: column;

`

const DesignCoinSection = styled.div`

    postition: absolute;
    padding: 300px 0 300px;
    z-index: 200;
    width: 100vw;
    display: flex;
    flex-direction: column;
    justify-content: center;
    // background-image: url(./img/wavy.png);

    h1 {
      font-size: 48px;
      margin-bottom: 30px;
    }

    h3 {
      font-size: 32px;
      margin-bottom: 40px;
      font-weight: 700;
    }

    h4 {
      margin-bottom: 30px;
      font-size: 24px;
    }

    .categoryBox {
      display: flex;
      flex-direction: column;
      align-items: center;
      margin: 20px;
    }

    .categories {

      margin: 0 auto;
      padding: 10px;
      width: auto;
      display: flex;

    }

    .servicesOffered {
      padding: 70px 30px;
      box-shadow: 0px 0px 5px rgba(0,0,0,.3);
      max-width: 900px;
      margin: 0 auto;
    }

    img {
      height: 150px;
      margin: 0 auto;
    }

`

const FooterText = styled.div`

    display: flex;
    border-top: 1px solid #e5e5e5;
    padding: 20px;
    justify-content: space-between;

    h3 {
      padding: 10px;
    }

`

const Footer = styled.div`
position: absolute;

